configuration.source:

def file = new File(project.properties['xml'])
if (!file.exists()) {
    log.info("No XML report, nothing to parse")
    return
}
def xml = new XmlParser().parse(file)
def bugs = xml.BugInstance
def total = bugs.size()
if (total <= 0) {
    log.info("No errors/warnings found")
    return
}
log.info("Total bugs: " + total)
for (i in 0..total-1) {
    def bug = bugs[i]
    log.info(
        bug.LongMessage.text()
        + " " + bug.Class.'@classname'
        + " " + bug.Class.SourceLine.Message.text()
    )
}
if (project.properties['failOnError']) {
    fail(total + " FindBugs error(s)")
}


import org.apache.commons.lang.StringUtils;
import org.apache.commons.io.FileUtils;
def target = new File(project.properties['target'])
if (!target.exists()) {
    fail("Can't find target: " + project.properties['target'])
}
for (module in project.modules) {
    def dir = new File(target.getPath(), module)
    dir.mkdir()
    for (ph in StringUtils.split(project.properties['placeholders'], ",")) {
        ph = StringUtils.trim(ph)
        def phfile = new File(ph);
        def url = getClass().getClassLoader().getResource(ph);
        if (url == null) {
            fail("Can't find placeholder " + ph)
        }
        log.info("Placeholder " + ph + " found at " + url);
        def dest = new File(
            dir.toString(),
            StringUtils.substringAfterLast(ph, "/")
        )
        if (dest.exists()) {
            fail("Placeholder already exists: " + dest)
        }
        FileUtils.copyURLToFile(
            url, // JAR-related URL of the found file
            dest // destination file
        )
        log.info("Placeholder " + ph + " copied to " + dir);
    }
}


import org.apache.commons.lang.StringUtils;
import org.apache.commons.io.FileUtils;
import org.apache.commons.io.FilenameUtils;
import org.apache.commons.exec.CommandLine;
import org.apache.commons.exec.DefaultExecutor;
def srcDir = new File(project.properties['src'])
def outputDir = new File(project.properties['output'])
if (!srcDir.exists()) {
    log.info("Directory is absent, nothing to compile: " + srcDir)
    return
}
log.info("Reading TIKZ files from " + srcDir)
if (!outputDir.exists()) {
    FileUtils.forceMkdir(outputDir)
    log.info("Directory created: " + outputDir)
}
log.info("Writing PNG files to " + outputDir)
tempDir = new File(project.properties['temp'])
FileUtils.forceMkdir(tempDir)
FileUtils.copyDirectory(srcDir, tempDir)
for (closure in StringUtils.split(project.properties['closures'], ",")) {
    def cfile = new File(closure);
    if (!cfile.exists()) {
        def url = getClass().getClassLoader().getResource(closure);
        if (url == null) {
            fail("Can't find closure " + closure)
        }
        log.info("Closure " + closure + " found at " + url);
        FileUtils.copyURLToFile(
            url, // JAR-related URL of the found file
            new File(
                tempDir.toString(),
                StringUtils.substringAfterLast(closure, "/")
            )
        )
    } else {
        FileUtils.copyFileToDirectory(cfile, tempDir)
    }
    log.info("Closure " + closure + " copied to " + tempDir);
}
def files = FileUtils.listFiles(srcDir, ["tex"] as String[], false)
for (src in files) {
    if (!FilenameUtils.getBaseName(src.getPath()).contains("-")) {
        continue
    }
    def dest = new File(
        outputDir.getPath(),
        FilenameUtils.getBaseName(src.getPath())
        + ".png"
    )
    if (dest.exists() && !FileUtils.isFileNewer(src, dest)) {
        continue
    }
    FileUtils.copyFile(src, new File(tempDir.getPath(), "a.tex"))
    def cmd = new String(
        "latex -halt-on-error -interaction=nonstopmode a.tex && "
        + "dvips -o a.ps a.dvi && "
        + "echo quit "
        + "| gs -q -dNOPAUSE -sDEVICE=ppmraw -sOutputFile=- -r300 a.ps"
        + "| pnmalias -bgcolor rgb:ff/ff/ff -falias -fgcolor rgb:00/00/00 -weight 0.6"
        + "| pnmcrop -white"
        + "| pnmscale " + project.properties['scale']
        + "| pnmtopng -interlace > a.png"
    )
    def line = new CommandLine("/bin/sh")
    line.addArgument("-c")
    line.addArgument(cmd, false)
    def executor = new DefaultExecutor()
    executor.setWorkingDirectory(tempDir)
    executor.execute(line);
    FileUtils.copyFile(new File(tempDir.getPath(), "a.png"), dest)
}
FileUtils.deleteDirectory(tempDir)


import org.apache.commons.io.FileUtils;
import org.apache.commons.io.filefilter.*;
import org.apache.commons.lang.StringUtils;
def dir = new File(project.properties['target'])
if (!dir.exists()) {
    log.info("Directory is absent: " + dir)
    return
}
def it = FileUtils.iterateFiles(
    dir,
    new SuffixFileFilter(".html"),
    TrueFileFilter.INSTANCE
)
def find = StringUtils.stripAll(
    StringUtils.split(project.properties['find'], "\n")
)
def replace = StringUtils.stripAll(
    StringUtils.split(project.properties['replace'], "\n")
)
while (it.hasNext()) {
    def File file = it.next()
    def contents = FileUtils.readFileToString(file)
    contents = StringUtils.replaceEach(contents, find, replace)
    FileUtils.writeStringToFile(file, contents)
}
